import React,{useState, useEffect} from "react";
import app from "./base";
import { withRouter, Redirect } from "react-router";
import "./home.css"

import firebase from "firebase";

const Home = ({history}) => {
  const [key, setKey] = useState("")
  const [title, setTitle] = useState("")
  const [amount, setAmount] = useState(0)
  const [prescribe, setPrescribe] = useState("")
  const [image,setImage]=useState("")
  const [imageUrl,setImageUrl]=useState("")
  const [adsimage,setAdsimage]=useState("")
  const [ProductList, setProductList] = useState([]);
  const [currentItem, setCurrentItem] = useState({})


  var adminpanelRef = firebase.database().ref("/AdminPanel");
  const Push = (object) => {
   adminpanelRef
     .push(object)
     .then(() => {
       console.log("Data send");
       // window.location.reload(false);
     })
     .catch(alert);

     const Remove = (id) => {
      adminpanelRef.child(id).remove();
      window.location.reload(false); 
    }}

    useEffect(() => {
      adminpanelRef.once("value", function (snapshot) {
        var tutorials = [];
        console.log('second check')
        snapshot.forEach(function (childSnapshot) {
          var key = childSnapshot.key;
          var data = childSnapshot.val();
          console.log(childSnapshot.val());
          tutorials.push({
            id: key,
           title: data.title,
           amount:data.amount,
           prescribe:data.prescribe
          });
        });
        setProductList(tutorials);
        console.log(tutorials);
      });
    }, [])






  

  return (
    <div >
    <div className="papa">
     <button style={{float:"right"}} onClick={() => app.auth().signOut()}>Sign out</button>
  
 <div>
 <table className='title-table'>
             
            <tr className='heading-tbl-1' >
                    <th >Title</th>
                    <th>Amount</th>
                    <th>Prescribtion</th>
                    
                </tr>  
          {
            ProductList.map((data) => {
              return(
                  <tr className="uka-1">  
                    <td> { data.title }</td>
                    <td>{  data.amount }</td>
                    <td>{  data.prescribe }</td>
    
                    <td> <button 
                    onClick={() => {
                 Remove(data.id)
               }}
                    style={{backgroundColor:"red", borderRadius:"10px" }}> X </button></td>
              <td>
              <button onClick={()=>{
                setOpen(true)
                setTitle(data.title)
                setAmount(data.amount)
                setPrescribe(data.prescribe)
                setKey(data.id)
            }}> Edit </button>

              </td>
                  </tr>
              )
               
            
            })
          }
           </table>
 </div>


    </div>
     </div>
  );

}

export default Home;
