import React,{useState, useEffect} from "react";
import app from "./base";
import { withRouter, Redirect } from "react-router";


import firebase from "firebase";

const Home = ({history}) => {
  const [show, setshow] = useState(false)
  const [key, setkey] = useState("")
  const [Schoolname, setSchoolname] = useState("")
  const [Student, setStudent] = useState("")
  const [studentList, setStudentList] = useState([]);
  var studentRef = firebase.database().ref("/students");


  const Push = (object) => {
    studentRef
      .push(object)
      .then(() => {
        console.log("Data send");
        window.location.reload(false);
      })
      .catch(alert);
  };

  const Remove = (id) => {
    studentRef.child(id).remove();
    // window.location.reload(false); 
  };


  const handleClose = () => setshow(false);
  const handleShow = () => setshow(true);

  
  useEffect(()=>{
    studentRef.once("value", function (snapshot) {
      var tutorials = [];
      console.log('second check')
      snapshot.forEach(function (childSnapshot) {
        var key = childSnapshot.key;
        var data = childSnapshot.val();
        console.log(childSnapshot.val());
        tutorials.push({
          id: key,
          name: data.name,
          schoolName: data.schoolname
        });
      });
      setStudentList(tutorials);
      console.log(tutorials);
    });
  },[])

  return (
    <div style={{backgroundColor:"skyblue", height:"100%"}}>
      <h1>Home <button style={{float:"right"}} onClick={() => app.auth().signOut()}>Sign out</button></h1> 
      <form style={{  height:"100hw",color:"whitesmoke", display:"flex", flexDirection:"column", justifyContent:"center", alignItems:"center"  }}>

<input style={{borderRadius:"15px", height:"30px", width:"300px"}} type="text" placeholder="Enter School Name" onChange={(e)=>setSchoolname(e.target.value)} />
<br/>
<input style={{borderRadius:"15px", height:"30px", width:"300px"}}  type="text" placeholder="Enter Student Name " onChange={(e)=>setStudent(e.target.value)} />
     <br/>
      <button style={{backgroundColor:"red",borderRadius:"15px", height:"30px", width:"300px"}} onClick={()=>{
        Push({
          name:Student,
          schoolname:Schoolname
        })
      }} > Enter Data </button>
      <br/>
      <div onClick={
    ()=>  history.push("/Teacherroom")} style={{backgroundColor:"orange", color:"white", height:"30px", width:"250px", float:"right", display:"flex", flexDirection:"row", justifyContent:"center", alignItems:"center", borderRadius:"15px"}}> Teachers Entry</div>

<div onClick={
    ()=>  history.push("/upload")} style={{backgroundColor:"orange", color:"white", height:"30px", width:"250px", float:"right", display:"flex", flexDirection:"row", justifyContent:"center", alignItems:"center", borderRadius:"15px"}}> Photo Entry</div>

       {
         studentList.map((data)=>{
           return <div style={{
             border:"1px solid red",
             width:"200px",
             display: "flex",
             flexDirection: "row",
             justifyContent:"center",
            alignItems:"center"
           }}>
            <div>
               <h5>{data.name}</h5>
               <h6>{data.schoolName}</h6>
               <button onClick={() => {
                 Remove(data.id)
               }}>Delete</button>
               <button onClick={() => {
                  setkey(data.key)
                          setStudent(data.Student)
                          setSchoolname(data.Schoolname)
                          setshow(true);
               }}>Edit</button>
            </div>
           </div>
         })
       }
      </form>
     
     </div>
  );

}

export default Home;
